function signUp(o) {
    myDiv.style.display = `${o}`;
}
function saveAccount() {}
function addAccount() {
    if (!0 == testEmail()) {
        account = [];
        var o = { email: emailInput.value, passward: passwardInput.value };
        "" == emailInput.value
            ? alert("please type your mail")
            : (account.push(o), signUp("none"), (signUpBtn.innerHTML = "account"), (headLine.innerHTML = "Account Info"), (emailError.style.display = "none"), localStorage.setItem("account", JSON.stringify(o)));
    } else emailError.style.display = "block";
}
function testEmail() {
    return !0 == /^[a-zA-z_.1-90-90-9]{3,30}@(yahoo|gmail|outlook|hotmail)(.com|.sa)$/.test(emailInput.value);
}
function passwardTest() {
    return !0 == /^[a-z_a-z]{6,20}[1-9]{1,20}$/.test(passwardInput.value);
}
